pragma solidity 0.5.1;

import "./Utils/SigUtil.sol";
import "./Utils/SafeMath.sol";
import "./Utils/Role.sol";
import "./Utils/AddressManager.sol";
import "./Token/IToken.sol";
import "./IStakeManager.sol";


contract Btc2eth1 is AddressManager, Role {
    using SafeMath for uint256;

    mapping(bytes32 => bytes32) private orders;
    mapping(bytes32 => address) private wshmap; // sha256 hash
    mapping(bytes32 => bytes32) private lender;
    mapping(bytes32 => bytes32) private minter;
    mapping(bytes32 => uint256) private counts;
    mapping(bytes32 => uint256) private minted;
    mapping(address => bytes32) private stored;

    IToken private          btct;
    IStakeManager private   stma;
    uint256 private         step;

    constructor (address _btct, address _sm, uint256 _join) public {
        btct = IToken(_btct);
        stma = IStakeManager(_sm);
        step = step;
        _setOwner(msg.sender);
    }

    // infrom updated wsh to other witness via ipfs
    function keep(bytes32 _ipf) public notPaused {
        //require(sm.isSWC(msg.sender), "msg.sender != witness");
        stored[msg.sender] = _ipf;
        emit KeepEvent(msg.sender, _ipf);
    }

    // witness leader submit _wsh
    function addWsh(bytes32 _wsh) public notPaused {
        require(wshmap[_wsh] == address(0x0), "wsh is 0x0");
        wshmap[_wsh] = msg.sender;
        counts[_wsh] = 0;
        emit AddWshEvent(msg.sender, _wsh);
    }

    // lender htlc -> Witness secret -> go to treasury. Expired -> Bob pubkey -> refund
    // minter htlc -> bob secret -> refund to alice, Expired ->  go to treasury
    // signed by minter
    function entangleOne(bytes32 _wsh, bytes32 _lsh, uint256 _sats, uint256 _end, bytes32 _txOne) public notPaused {
        require(wshmap[_wsh] != address(0x0), "wsh is not 0x0");
        require(lender[_wsh] == 0x0, "wsh is already used for lender");
        lender[_wsh] = keccak256(abi.encodePacked(_wsh, _lsh, _sats, _end, _txOne, msg.sender));
        emit EntangleOneEvent(_wsh, _lsh, _sats, _end, _txOne);
    }

    function entangleA(bytes32 _wsh, bytes32 _txA) public notPaused {
        require(wshmap[_wsh] != address(0x0), "wsh is not 0x0");
        require(lender[_wsh] != 0x0, "wsh is already used");
        require(minter[_wsh] == 0x0, "wsh is already used");
        minter[_wsh] = keccak256(abi.encodePacked(_wsh, lender[_wsh], _txA, msg.sender));
    }

    function approveStepAB(bytes32 _wsh) public notPaused {
        require(wshmap[_wsh] != address(0x0), "wsh is not 0x0");
        require(lender[_wsh] != 0x0, "wsh is already used");
        require(minter[_wsh] != 0x0, "wsh is already used");
        counts[_wsh] = counts[_wsh].add(1);
    }

    function pon(bytes32 _wsh, bytes32 _lsh, uint256 _sats, uint256 _end, bytes32 _txB, bytes32 _txA) public notPaused {
        bytes32 lenderTxHash = keccak256(abi.encodePacked(_wsh, _lsh, _sats, _end, _txB, msg.sender));
        bytes32 minterTxHash = keccak256(abi.encodePacked(_wsh, lenderTxHash, _txA, msg.sender));
        require(lender[_wsh] == lenderTxHash);
        require(minter[_wsh] == minterTxHash);
        require(counts[_wsh] >= step);
        btct.mint(msg.sender, _sats);
        counts[_wsh] = 2 ** 256 - 1; // minted flag
    }

    function gon(bytes32 _wsh, uint256 _sats, uint256 _end, bytes32 _txB) public notPaused {
        bytes32 lenderTxHash = keccak256(abi.encodePacked(_wsh, _sats, _end, _txB, msg.sender));
        require(lender[_wsh] == lenderTxHash);
        require(counts[_wsh] == 2 ** 256 - 1);
        btct.transferFrom(msg.sender, address(this), _sats);
        btct.burn(address(this), _sats);
    }

    function ponLsh(bytes32 _wsh, uint256 _sats, uint256 _end, bytes32 _txB) public {
        bytes32 lenderTxHash = keccak256(abi.encodePacked(_wsh, _sats, _end, _txB, msg.sender));
    }

    function setPaused(bool _paused) public onlyOwner {
        _setPaused(_paused);
        btct.setPaused(_paused);
    }

    function getWshMap(bytes32 _wsh) public view returns (address) {
        return wshmap[_wsh];
    }

    function getKeep(address _who) public view returns (bytes32) {
        return stored[_who];
    }

    event AddWshEvent(address indexed own, bytes32 wsh);
    event KeepEvent(address indexed own, bytes32 ipf);
    event EntangleOneEvent(bytes32 wsh, bytes32 lsh, uint256 sats, uint256 end, bytes32 txOne);

}
