pragma solidity 0.5.1;

import "./Token/IToken.sol";
import "./Utils/SafeMath.sol";


contract Staker {
    using SafeMath for uint256;

    mapping(address => uint256) private stakes;
    mapping(address => address) private linked;
    mapping(address => bool)    private locked;
    uint256 private shares;  // minimum staking balance
    uint256 private spills;
    IToken private gov;

    function () external payable {
        revert();
    }

    function setup(address _gov, uint256 _shares) public {
        require(address(gov) == address(0x0));
        gov = IToken(_gov);
        shares = _shares * 10 ** uint256(gov.decimals());
    }

    function deposit(address _from, uint256 _amount) public returns (bool) {
        // _from is not linked
        require(linked[_from] == address(0x0));
        gov.transferFrom(_from, address(this), _amount);
        stakes[_from] = stakes[_from].add(_amount);
        emit DepositEvent(_from, _amount);
        return true;
    }

    function withdraw(uint256 _amount) public returns (bool) {
        require(!isLocked(msg.sender));
        stakes[msg.sender] = stakes[msg.sender].sub(_amount);
        gov.transfer(msg.sender, _amount);
        emit WithdrawEvent(msg.sender, _amount);
        return true;
    }

    function depute(address _who) public returns (bool) {
        require(!isLocked(msg.sender));
        require(getStake(_who) == 0);
        require(getStake(msg.sender) >= shares);
        lock(msg.sender);
        linked[_who] = msg.sender;
        emit DeputeEvent(msg.sender, _who);
        return true;
    }

    function resign(address _who) public returns (bool) {
        require(isLocked(msg.sender));
        require(linked[_who] == msg.sender);
        unlock(msg.sender);
        linked[_who] = address(0x0);
        emit ResignEvent(msg.sender, _who);
        return true;
    }

    function spend(uint256 _amount) public returns (bool) {
        stakes[msg.sender] = stakes[msg.sender].sub(_amount);
        spills = spills.add(_amount);
        emit SpendEvent(msg.sender, _amount);
        return true;
    }

    function getGov() public view returns (address) {
        return address(gov);
    }

    function getStake(address _who) public view returns (uint256) {
        if (_who == address(0x0)) {
            return 0;
        }
        return stakes[_who];
    }

    function getTotalSupply() internal view returns (uint256) {
        return gov.totalSupply();
    }

    function getDecimals() internal view returns (uint256) {
        return gov.decimals();
    }

    function getLink(address _who) internal view returns (address) {
        return linked[_who];
    }

    function isLocked(address _who) internal view returns (bool) {
        return locked[_who];
    }

    function lock(address _who) internal {
        locked[_who] = true;
    }

    function unlock(address _who) internal {
        locked[_who] = false;
    }

    function _slice(address _who, uint256 _amount) internal returns (bool) {
        // add reward 1/10 of _amount cost;
        if (spills >= _amount.div(100)) {
            stakes[_who] = stakes[_who].add(_amount.div(100));
            spills = spills.sub(_amount.div(100));
        }
    }

    event DepositEvent(address _who, uint256 _amount);
    event WithdrawEvent(address _who, uint256 _amount);
    event DeputeEvent(address _owner, address _who);
    event ResignEvent(address _owner, address _who);
    event SpendEvent(address _who, uint256 _amount);
}
